import React, { useState } from "react";
import styles from "./InformationBox.module.scss";
import {
	IconFeedBack,
	IconSearch,
	IconClose,
	IconLan,
	IconSend,
	IconAdd,
} from "../Icon/Icon";

const InformationBox = () => {
	const [isShow, setIsShow] = useState(false);

	const handleClick = () => {
		setIsShow(!isShow);
	};
	return (
		<div className={styles.infoWrapper}>
			<div className={styles.infoWrapperInner}>
				<div className={styles.wrapperTitleBox}>
					<div className={styles.title}>
						<span>
							<IconFeedBack />
						</span>
						問題和討論
					</div>
					<div className={styles.searchBox}>
						{isShow ? <input type="text" placeholder="搜尋討論" /> : null}
						{!isShow ? (
							<span onClick={() => handleClick()}>
								<IconSearch />
							</span>
						) : null}
						{isShow ? (
							<span onClick={() => handleClick()}>
								<IconClose />
							</span>
						) : null}
					</div>
				</div>
				<div className={styles.addNew}>
					<div className="box">
						<span>
							<IconAdd />
						</span>
						<span className={styles.text}>新增討論</span>
					</div>

					<div className={styles.addMessage}>
						<div className={styles.avatar}></div>
						<div className="textAreaBox">
							<textarea placeholder="新增回覆" />
							<span>
								<IconSend />
							</span>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};

export default InformationBox;
