import React, { useState, useContext } from "react";
import Layout from "../Layout/Layout";
import VideoBox from "../components/VideoBox/VideoBox";
import CommentBox from "../components/CommentBox/CommentBox";
import ClassTitleWrapper from "../components/ClassTitleWrapper/ClassTitleWrapper";
import ShareWrapper from "../components/ShareWrapper/ShareWrapper";
import Grid from "@material-ui/core/Grid";
import { Provider, discussionListContext } from "../components/context";

const Index = () => {
	console.log(discussionListContext);
	// const contextValue = { generateDiscussionList, generateReplyDiscussion };
	return (
		<div className="index">
			<Grid container spacing={2}>
				<Grid item lg={8}>
					<VideoBox />
				</Grid>
				<Grid item lg={4} style={{ padding: "0 8px" }}>
					<Provider value={discussionListContext}>
						<CommentBox />
					</Provider>
				</Grid>
				<Grid item lg={8}>
					<ClassTitleWrapper />
					<ShareWrapper />
				</Grid>
			</Grid>
		</div>
	);
};

export default Index;
