import { createContext } from "react";
export const defaultDiscussionList = [
	{
		record: {
			name: "",
			date: "",
			content: "",
			reply: [
				{
					record: {
						name: "",
						date: "",
						content: "",
					},
				},
			],
		},
	},
];
const context = createContext();
export const { Provider, Consumer } = context;
export default context;
