import React, { useState } from "react";
import styles from "./InformationBox.module.scss";
import avatarStyle from "../../styles/components/avatar.module.scss";
import buttonStyle from "../../styles/components/buttons.module.scss";
import {
	IconFeedBack,
	IconSearch,
	IconClose,
	IconLan,
	IconSend,
	IconAdd,
} from "../Icon/Icon";
import Icon from "@material-ui/core/Icon";
import IconButton from "@material-ui/core/IconButton";
import { makeStyles } from "@material-ui/core/styles";

const InformationBox = () => {
	const [isShow, setIsShow] = useState(false);

	const handleClick = () => {
		setIsShow(!isShow);
	};
	const useStyles = makeStyles(() => ({
		roots: {
			fontSize: "14px",
			borderRadius: "50%",
		},
		iconColorRoots: {
			color: "#fff",
		},
	}));
	const classes = useStyles();
	return (
		<div className={styles.infoWrapper}>
			<div className={styles.infoWrapperInner}>
				<div className={styles.wrapperTitleBox}>
					<div className={styles.title}>
						<span>
							<IconFeedBack />
						</span>
						問題和討論
					</div>
					<div className={styles.searchBox}>
						{isShow ? <input type="text" placeholder="搜尋討論" /> : null}
						{!isShow ? (
							<span onClick={() => handleClick()}>
								<IconSearch />
							</span>
						) : null}
						{isShow ? (
							<span onClick={() => handleClick()}>
								<IconClose />
							</span>
						) : null}
					</div>
				</div>
				<div className={styles.addNew}>
					<div className={styles.addNewBox}>
						<span>
							<IconAdd />
						</span>
						<span className={styles.text}>新增討論</span>
					</div>
					<div className={styles.addMessage}>
						<div className={avatarStyle.avatarMl}>E</div>
						<div className={styles.textAreaBox}>
							<textarea placeholder="新增回覆" />
						</div>
						<IconButton className={classes.roots} size="small" color="inherit">
							<Icon className={classes.iconColorRoots}>send</Icon>
						</IconButton>
					</div>
				</div>
			</div>
		</div>
	);
};

export default InformationBox;
