import "./CommentList.module.scss";
import React, { Fragment, useContext } from "react";
import Comment from "../Comment/Comment";
import context from "../context";
import { Provider } from "../context";

const CommentList = () => {
	const contextValue = useContext(context);
	const { discussionListData } = contextValue;
	const { discussionList } = discussionListData;
	console.log(discussionList, "discussionList");
	console.log(discussionListData, "discussionListData");
	return (
		<Fragment>
			{discussionListData
				? discussionListData.map((item, i) => (
						<Comment discussionData={item} index={i} />
				  ))
				: null}
		</Fragment>
	);
};

export default CommentList;
