import styles from "./CommentBox.module.scss";
import avatarStyle from "../../styles/components/avatar.module.scss";
import buttonStyle from "../../styles/components/buttons.module.scss";
import commentStyle from "../../styles/components/comment.module.scss";
import React, { useState } from "react";
import CommentList from "../CommentList/CommentList";
import CommentForm from "../CommentForm/CommentForm";
import { IconFeedBack, IconSearch, IconClose, IconAdd } from "../Icon/Icon";

const InformationBox = () => {
	const [show, setShow] = useState(false);
	const [comment, setComment] = useState(false);

	const handleClick = () => {
		setShow(!show);
	};
	const handleComment = () => {
		setComment(!comment);
	};

	const generateDiscussionList = {
		discussionList: [
			{
				record: {
					name: "Stanley",
					date: "2020/07/16",
					content: "goood!",
				},
				record: {
					name: "Trump",
					date: "2020/07/17",
					content: "oh ya is good!",
				},
			},
		],
	};
	return (
		<div className={styles.infoWrapper}>
			<div className={styles.infoWrapperInner}>
				<div className={styles.wrapperTitleBox}>
					<div className={styles.title}>
						<span>
							<IconFeedBack />
						</span>
						問題和討論
					</div>
					<div className={styles.searchBox}>
						{show ? <input type="text" placeholder="搜尋討論" /> : null}
						{show ? (
							<span onClick={() => handleClick()}>
								<IconClose />
							</span>
						) : (
							<span onClick={() => handleClick()}>
								<IconSearch />
							</span>
						)}
					</div>
				</div>
				<div className={styles.addNew}>
					<div className={styles.addNewBox}>
						<span>
							<IconAdd />
						</span>
						<span className={styles.text} onClick={handleComment}>
							新增討論
						</span>
					</div>
					{comment ? <CommentForm /> : null}
				</div>
				<div className={styles.commentList}>
					<div className={styles.totalComment}>3個討論</div>
					<CommentList />
				</div>
			</div>
		</div>
	);
};

export default InformationBox;
