import styles from "./Comment.module.scss";
import avatarStyle from "../../styles/components/avatar.module.scss";
import React, { useState } from "react";
import { IconCheckCircle } from "../Icon/Icon";
import CommentForm from "../CommentForm/CommentForm";

const Comment = ({ discussionData, index }) => {
	console.log("discussionData", discussionData);
	const { content, date, name, reply } = discussionData;
	const [feedBack, setFeedBack] = useState(false);
	const handleFeedback = () => {
		setFeedBack(!feedBack);
	};

	return (
		<div className={styles.comment}>
			<div className={avatarStyle.avatarMl}>E</div>
			<div className={styles.commentWrapper}>
				<div className={styles.commentBox}>
					<div className={styles.nameText}>
						<div>{name}</div>
						<div>{date}</div>
					</div>
					<div className={styles.rightContent}>
						<span className={styles.iconBox}>
							<IconCheckCircle />
						</span>
						<span className={styles.text}>已解決</span>
					</div>
				</div>
				<div className={styles.commentBox}>
					<span className={styles.commentContent}>{content}</span>
					<div className={styles.rightContent}>
						<span className={styles.lattice}>+1</span>
						<span className={styles.number}>0</span>
					</div>
				</div>
				<div className={styles.feedback}>
					<div className={styles.feedbackEvent}>
						<div className={avatarStyle.avatarSm || styles.avatar}>E</div>
						<div
							className={styles.feedbackText}
							onClick={() => handleFeedback()}
						>
							回覆
						</div>
					</div>
					{feedBack ? <CommentForm /> : null}
				</div>
			</div>
		</div>
	);
};

export default Comment;
