import React from "react";
import styles from "./VideoBox.module.scss";
import ReactPlayer from "react-player/lazy";
import { IconButton } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import VolumeUpIcon from "@material-ui/icons/VolumeUp";
import Slider from "@material-ui/core/Slider";
import VolumeDown from "@material-ui/icons/VolumeDown";
import VolumeUp from "@material-ui/icons/VolumeUp";
const VideoBox = () => {
	const [value, setValue] = React.useState(30);

	const handleChange = (event, newValue) => {
		setValue(newValue);
	};
	return (
		<Grid container spacing={2} className={styles.videoWrapper}>
			<Grid xs={12} className="videoPlayBox">
				<ReactPlayer
					url="https://youtu.be/xjadNS2HBpM"
					className="player"
					width={"100%"}
					height={500}
					autoPlay={false}
					controls={true}
					volume={1}
					muted={false}
				/>
			</Grid>
			<Grid container spacing={2} className={styles.playController}>
				<Grid item xs>
					<IconButton style={{ color: "#fff" }}>
						<PlayArrowIcon fontSize="large" style={{ color: "#fff" }} />
					</IconButton>
				</Grid>
				<Grid item>
					<VolumeDown style={{ color: "#fff" }} />
				</Grid>
				<Grid item xs={2}>
					<Slider
						value={value}
						onChange={handleChange}
						aria-labelledby="continuous-slider"
						style={{ color: "#fff" }}
					/>
				</Grid>
				<Grid item>
					<VolumeUp style={{ color: "#fff" }} />
				</Grid>
			</Grid>
		</Grid>
	);
};

export default VideoBox;
