import CommentList from "../CommentList/CommentList";
import SendIcon from "@material-ui/icons/Send";
import { IconButton } from "@material-ui/core";
import {
	IconFeedBack,
	IconSearch,
	IconClose,
	IconAdd,
	IconCheckCircle,
} from "../Icon/Icon";

const InformationBox = () => {
	const [show, setShow] = useState(false);
	const [comment, setComment] = useState(false);
	const [feedBack, setFeedBack] = useState(false);

	const handleClick = () => {
		setShow(!show);
	};
	const handleComment = () => {
		setComment(!comment);
	};
	const handleFeedback = () => {
		setFeedBack(!feedBack);
	};

	const generateDiscussionList = {
		discussionList: [
			{
				record: {
					name: "Stanley",
					date: "2020/07/16",
					content: "goood!",
				},
				record: {
					name: "Trump",
					date: "2020/07/17",
					content: "oh ya is good!",
				},
			},
		],
	};
	return (
		<div className={styles.infoWrapper}>
			<div className={styles.infoWrapperInner}>
				<div className={styles.wrapperTitleBox}>
					<div className={styles.title}>
						<span>
							<IconFeedBack />
						</span>
						問題和討論
					</div>
					<div className={styles.searchBox}>
						{show ? <input type="text" placeholder="搜尋討論" /> : null}
						{show ? (
							<span onClick={() => handleClick()}>
								<IconClose />
							</span>
						) : (
							<span onClick={() => handleClick()}>
								<IconSearch />
							</span>
						)}
					</div>
				</div>
				<div className={styles.addNew}>
					<div className={styles.addNewBox}>
						<span>
							<IconAdd />
						</span>
						<span className={styles.text} onClick={() => handleComment()}>
							新增討論
						</span>
					</div>
					{comment ? (
						<div className={styles.addMessage}>
							<div className={avatarStyle.avatarMl}>E</div>
							<div className={styles.textAreaBox}>
								<textarea value={discussionList} placeholder="新增回覆" />
							</div>
							<button className={buttonStyle.iconButtonSm || styles.button}>
								<IconButton>
									<SendIcon fontSize="small" />
								</IconButton>
							</button>
						</div>
					) : null}
				</div>
				<div className={styles.comment}>
					<div className={styles.totalComment}>3個討論</div>
					<CommentList />
