import React, { useContext, useState } from "react";
import avatarStyle from "../../styles/components/avatar.module.scss";
import buttonStyle from "../../styles/components/buttons.module.scss";
import styles from "./CommentForm.module.scss";
import context from "../context";
import SendIcon from "@material-ui/icons/Send";
import { IconButton } from "@material-ui/core";

const CommentForm = () => {
	const contextValue = useContext(context);
	const { discussionList, setDiscussionList } = contextValue;
	// const { handleCommentSubmit } = contextValue;
	// const [replyValue, setReplyValue] = useState("");

	// const handleChange = (event) => {
	// 	setReplyValue(event.target.value);
	// };

	const handleSubmit = (event) => {
		event.preventDefault();
		const commentItem = {
			record: {
				name: "",
				date: "",
				content: "",
				reply: [
					{
						record: {
							name: "",
							date: "",
							content: "",
						},
					},
				],
			},
		};
		discussionList = [...commentItem];

		setDiscussionList([...discussionList]);
		console.log(discussionList);
		// const content = replyValue;
		// const name = "TESTName";
		// const date = "2020/07/16";
		// handleCommentSubmit({ record: { name, date, content } });
	};
	return (
		<form onSubmit={handleSubmit}>
			<div className={styles.addMessage}>
				<div className={avatarStyle.avatarMl}>E</div>
				<div className={styles.textAreaBox}>
					<textarea placeholder="新增回覆" />
				</div>
				<button className={buttonStyle.iconButtonSm}>
					<IconButton>
						<SendIcon fontSize="small" />
					</IconButton>
				</button>
			</div>
		</form>
	);
};

export default CommentForm;
