import avatarStyle from "../../styles/components/avatar.module.scss";
import styles from "./Comment.module.scss";
import React, { Fragment, useContext } from "react";
import CommentReply from "../CommentReply/CommentReply";
import context from "../context";

const Comment = ({ index }) => {
	const contextValue = useContext(context);
	console.log(contextValue);
	const { discussionList } = contextValue;
	console.log(discussionList);
	const { name, content, date } = discussionList.record[index];

	return (
		<div className={styles.comment}>
			<div className={avatarStyle.avatarMl}>{name.substring(0, 1)}</div>
			<div className={styles.commentWrapper}>
				<div className={styles.commentBox}>
					<div className={styles.nameText}>
						<div>{name}</div>
						<div>{date}</div>
					</div>
				</div>
				<div className={styles.commentBox}>
					<span className={styles.commentContent}>{content}</span>
					<div className={styles.rightContent}>
						<span className={styles.lattice}>+1</span>
						<span className={styles.number}>0</span>
					</div>
				</div>
				{/* <CommentReply
					key={`reply${index}`}
					replyItem={replyItem}
					index={index}
				/> */}
			</div>
		</div>
	);
};

export default Comment;
