import React, { useState, useContext } from "react";
import Layout from "../Layout/Layout";
import VideoBox from "../components/VideoBox/VideoBox";
import CommentBox from "../components/CommentBox/CommentBox";
import ClassTitleWrapper from "../components/ClassTitleWrapper/ClassTitleWrapper";
import ShareWrapper from "../components/ShareWrapper/ShareWrapper";
import Grid from "@material-ui/core/Grid";
import { Provider, defaultDiscussionList } from "../components/context";

const Index = () => {
	const [discussionList, setDiscussionList] = useState([
		...defaultDiscussionList,
	]);
	console.log(discussionList);
	const commentContext = {
		discussionList,
		setDiscussionList,
	};
	return (
		<Provider value={commentContext}>
			<div className="index">
				<Grid container spacing={2}>
					<Grid item lg={8}>
						<VideoBox />
					</Grid>
					<Grid item lg={4} style={{ padding: "0 8px" }}>
						{/* <CommentBox /> */}
					</Grid>
					<Grid item lg={8}>
						<ClassTitleWrapper />
						<ShareWrapper />
					</Grid>
				</Grid>
			</div>
		</Provider>
	);
};

export default Index;
