import React, { useEffect, useState } from "react";
import { IconLan } from "../../components/Icon/Icon";
import avatarStyle from "../../styles/components/avatar.module.scss";
import { IconButton } from "@material-ui/core";
import LanguageIcon from "@material-ui/icons/Language";
import Logo from "../../public/static/academy-logo.svg";

const Header = () => {
	const [hideUp, setHideUp] = useState("");
	useEffect(() => {
		window.addEventListener("scroll", handleScroll);
	});

	const handleScroll = () => {
		let lastScrollY = 0;
		const scrollTop =
			document.documentElement.scrollTop ||
			window.pageYOffset ||
			document.body.scrollTop;
		if (scrollTop < lastScrollY) {
			setHideUp("hideUp");
		} else {
			setHideUp("");
		}
		console.log(scrollTop, "scrollTop");
		console.log(lastScrollY, "lastScrollY");
	};

	return (
		<header id="header" className={`header ${hideUp}`}>
			<div className="header-inner">
				<a className="logo" href="/">
					<div className="logo-Box">
						<Logo />
					</div>
					<div className="logo-text">Academy</div>
				</a>
				<nav>
					<ul>
						<li>
							<a href="/">探索</a>
						</li>
						<li>
							<a href="/">我的課程</a>
						</li>
					</ul>
				</nav>
				<div className="person-box">
					<IconButton>
						<LanguageIcon fontSize="large" />
					</IconButton>
					<div className={avatarStyle.avatarMl}>E</div>
				</div>
			</div>
		</header>
	);
};

export default Header;
