import React from "react";
import styles from "./VideoBox.module.scss";
import ReactPlayer from "react-player/lazy";
import { IconButton } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import Slider from "@material-ui/core/Slider";
import VolumeDown from "@material-ui/icons/VolumeDown";
import VolumeUp from "@material-ui/icons/VolumeUp";
import ClosedCaptionIcon from "@material-ui/icons/ClosedCaption";
import CropFreeIcon from "@material-ui/icons/CropFree";
const VideoBox = () => {
	const [value, setValue] = React.useState(30);

	const handleChange = (event, newValue) => {
		setValue(newValue);
	};
	return (
		<Grid container spacing={2} className={styles.videoWrapper}>
			<Grid xs={12} className="videoPlayBox">
				<ReactPlayer
					url="https://youtu.be/xjadNS2HBpM"
					className="player"
					width={"100%"}
					height={500}
					autoPlay={false}
					controls={true}
					volume={1}
					muted={false}
				/>
			</Grid>
			<Grid container spacing={2} className={styles.playController}>
				<div className={styles.playAndVolume}>
					<Grid item xs>
						<IconButton style={{ color: "#fff" }}>
							<PlayArrowIcon fontSize="large" style={{ color: "#fff" }} />
						</IconButton>
					</Grid>
					<VolumeDown className={styles.volume} style={{ color: "#fff" }} />
					<Slider
						className={styles.volumeMiddle}
						value={value}
						onChange={handleChange}
						style={{ color: "#fff" }}
					/>
					<VolumeUp className={styles.volume} style={{ color: "#fff" }} />
				</div>
				<div className={styles.rightController}>
					<IconButton style={{ color: "#fff" }}>
						<ClosedCaptionIcon fontSize="large" style={{ color: "#fff" }} />
					</IconButton>
					<IconButton style={{ color: "#fff" }}>
						<CropFreeIcon fontSize="large" style={{ color: "#fff" }} />
					</IconButton>
				</div>
			</Grid>
			<div className={styles.videoMarker}>
				<ul>
					<li>Signup</li>
					<li>Explore</li>
					<li>Course Preview</li>
					<li>Video & Markers</li>
				</ul>
			</div>
		</Grid>
	);
};

export default VideoBox;
