import styles from "./CommentForm.module.scss";
import avatarStyle from "../../styles/components/avatar.module.scss";
import buttonStyle from "../../styles/components/buttons.module.scss";
import React, { useContext, useState } from "react";
import SendIcon from "@material-ui/icons/Send";
import { IconButton } from "@material-ui/core";
import context from "../context";

const CommentForm = () => {
	const contextValue = useContext(context);
	const { handelCommentSubmit } = contextValue;
	const [reply, setReply] = useState("");

	const handleSubmit = (e) => {
		let value = setReply(e.target.value);
		let content = value;
		let name = "Jh";
		let date = new Date();
		console.log(content, "record");
		handelCommentSubmit({ record: { content, date, name } });
	};
	return (
		<form onSubmit={(e) => handleSubmit(e)}>
			<div className={styles.addMessage}>
				<div className={avatarStyle.avatarMl}>E</div>
				<div className={styles.textAreaBox}>
					<textarea
						value={reply}
						onChange={handleSubmit}
						placeholder="新增回覆"
					/>
				</div>
				<button className={buttonStyle.iconButtonSm}>
					<IconButton>
						<SendIcon fontSize="small" />
					</IconButton>
				</button>
			</div>
		</form>
	);
};

export default CommentForm;
