import "./CommentForm.module.scss";
import React from "react";

const CommentForm = () => {
	return (
		<div className={commentStyle.addMessage}>
			<div className={avatarStyle.avatarMl}>E</div>
			<div className={commentStyle.textAreaBox}>
				<textarea placeholder="新增回覆" />
			</div>
			<button className={buttonStyle.iconButtonSm || styles.button}>
				<IconButton>
					<SendIcon fontSize="small" />
				</IconButton>
			</button>
		</div>
	);
};

export default CommentForm;
